using System;
using System.Collections;
using AdvancedSceneManager.Utility;
using Lazy.Utility;
using UnityEngine;
using UnityEngine.UI;

namespace AdvancedSceneManager.Defaults
{

    /// <summary>A default splash screen script. Fades splash screen in and out.</summary>
    [ExecuteAlways]
    [AddComponentMenu("")]
    public class ASM_SplashScreen : Callbacks.SplashScreen
    {

        public CanvasGroup group;
        public Image background;
        public Image logo;
        public float fadeDuration = 1;
        public float waitDuration = 2.5f;

        #region Image

        const string base64Image = "";

        #endregion

        void SetImage()
        {

            var texture = new Texture2D(1, 1);
            var bytes = Convert.FromBase64String(base64Image);
            texture.LoadImage(bytes);
            var sprite = Sprite.Create(texture, new Rect(0, 0, texture.width, texture.height), new Vector2());

            logo.sprite = sprite;

        }

        void Start()
        {

            group.alpha = 0;
            //Use same color as unity splash screen, if enabled, defaults to black otherwise
            background.color = SceneManager.settings.project.buildUnitySplashScreenColor;

            SetImage();

        }

        void Update()
        {
            //Skip splash screen when any of the buttons are pressed
            if (IsSkipButtonPressed())
                coroutine?.Stop();
        }

        bool IsSkipButtonPressed()
        {

#if UNITY_2022_1_OR_NEWER && !ASM_LEGACY
            //Is used to run tests quicker
            if (SceneManager.app.currentProps?.softSkipSplashScreen ?? false)
                return true;
#endif

#if INPUTSYSTEM
            return (UnityEngine.InputSystem.Keyboard.current?.spaceKey?.isPressed ?? false) ||
                (UnityEngine.InputSystem.Keyboard.current?.escapeKey?.isPressed ?? false) ||
                (UnityEngine.InputSystem.Mouse.current?.leftButton?.isPressed ?? false) ||
                (UnityEngine.InputSystem.Gamepad.current?.aButton?.isPressed ?? false);
#else
            return Input.GetKeyDown(KeyCode.Space) || Input.GetKeyDown(KeyCode.Escape) || Input.GetMouseButton(0);
#endif

        }

        GlobalCoroutine coroutine;

        public override IEnumerator DisplaySplashScreen()
        {

            coroutine?.Stop();
            //Run the coroutines in a 'chain', this makes it easier to cancel splash screen on skip button
            coroutine = CoroutineUtility.Chain(
                () => group.Fade(1, fadeDuration),
                () => new WaitForSecondsRealtime(waitDuration),
                () => group.Fade(0.01f, fadeDuration));

            yield return coroutine;
            group.alpha = 0;

        }

    }

}
